import React, {Component} from 'react';
import moment from 'moment';

export default class MainContainer extends Component {

    constructor(props){
        super(props);
        this.state={
            projects:[]
        };
    }

    componentDidMount(){
        fetch('https://murmuring-tor-86528.herokuapp.com/projects')
        .then(response => {
            if (!response.ok) {
              throw new Error(response.statusText);
            }
            return response.json();
          })
        .then(data=>{
            this.setState({
                projects: data
            })
        }).catch(err=>{
            console.log(err);
        });
    }


    render(){
        return(
            <div className="panel panel-default">
                <div className="panel-heading">
                    <h3 className="panel-title">Projects</h3>
                </div>
                <div className="panel-body">
                    <div className="table-responsive overview-table">
                    <table className="table table-striped table-bordered">
                        <tbody>
                        {this.state.projects.map(project => {
                            let activeDay = moment().diff(project.ProjectStartDate,'day');
                            return(
                                <tr key={project._id}>
                                    <td>{project.ProjectName}</td>
                                    <td>Active {activeDay} days</td>
                                </tr> 
                            );
                        })}
                        </tbody>
                    </table>
                    </div>
                    {/* <Link to="/projects" className="btn btn-primary form-control">View All Project Data</Link> */}

                <a href="/projects" className="btn btn-primary form-control">View All Project Data</a>
                </div>
            </div>
        )
    }
}
