import React, {Component} from 'react';

export default class TeamsPanel extends Component {

    constructor(props){
        super(props);
        this.state={
            teams:[]
        };
    }

    componentDidMount(){
        fetch('https://murmuring-tor-86528.herokuapp.com/teams')
        .then(response => {
            if (!response.ok) {
              throw new Error(response.statusText);
            }
            return response.json();
          })
        .then(data=>{
            this.setState({
                teams: data
            })
        }).catch(err=>{
            console.log(err);
        });
    }

    render(){
        return(
            <div className="panel panel-default">
                <div className="panel-heading">
                    <h3 className="panel-title">Teams</h3>
                </div>
                <div className="panel-body">
                    <div className="table-responsive overview-table">
                    <table className="table table-striped table-bordered">
                        <tbody>
                        {this.state.teams.map(teams=>{
                            return(
                                <tr key={teams._id}>
                                    <td>{teams.TeamName}</td>
                                    <td>{teams.Employees.length}</td>
                                </tr>
                            );
                            })} 
                        </tbody>
                    </table>
                    </div>
                    <a href="/teams" className="btn btn-primary form-control">View All Team Data</a>
                </div>
            </div>
        )
    }
}
