import React, {Component} from 'react';
import {Link} from 'react-router-dom';


export default class TeamsPanel extends Component {

    constructor(props){
        super(props);
        this.state={
            teams:[]
        };
    }


    sortTeam(a,b){
        return a._id - b._id;
    }



    componentDidMount(){
        fetch('https://murmuring-tor-86528.herokuapp.com/teams')
        .then(response => {
            if (!response.ok) {
              throw new Error(response.statusText);
            }
            return response.json();
          })
        .then(data=>{
            data.sort(this.sortTeam);
            this.setState({
                teams: data.sort(function (a, b) {
                    return a._id - b._id;
                  })
            })
            console.log(this.state.teams);
        }).catch(err=>{
            console.log(err);
        });
    }

    render(){
        return(
            <div className="panel panel-default">
                <div className="panel-heading">
                    <h3 className="panel-title">Teams</h3>
                </div>
                <div className="panel-body">
                    <div className="table-responsive overview-table">
                    <table className="table table-striped table-bordered">
                        <tbody>
                        {this.state.teams.map(teams=>{
                            return(
                                <tr key={teams._id}>
                                    <td>{teams.TeamName}</td>
                                    <td>{teams.Employees.length} Employees</td>
                                </tr>
                            );
                            })} 
                        </tbody>
                    </table>
                    </div>
                    <Link to="/teams" className="btn btn-primary form-control">View All Team Data</Link>
                </div>
            </div>
        )
    }
}
